mapper.update:
UPDATE product
prod_name = #{prodName : VARCHAR}, prod_detail = #{prodDetail : VARCHAR}, manufacture_day = #{manuDate :VARCHAR}, price = #{price : NUMERIC}, image_file = #{fileName : VARCHAR}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT COUNT(*) FROM( SELECT p.*, t.tran_status_code FROM product p, transaction t WHERE p.prod_no = t.prod_no(+) AND t.tran_status_code is null
AND p.prod_name LIKE '%${searchKeyword}%'
) countTable
