mapper.update:
UPDATE product
prod_name = #{prodName}, prod_detail = #{prodDetail}, manufacture_day = #{manuDate}, price = #{price}, image_file = #{fileName : VARCHAR}, stock = #{stock}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT COUNT(*) FROM( SELECT p.*, t.tran_status_code FROM product p, transaction t WHERE p.prod_no = t.prod_no(+) AND t.tran_status_code is null
AND p.prod_name LIKE '%${searchKeyword}%'
) countTable
